const {Actor} = require("cqrs");

class User extends Actor{
  constructor(data = {}){
    const {name} = data;
    if(name && name.length > 2)
    super(data);
    else {
      throw new Error("create error!")
    }
  }
  changename(name){
    this.$({name});
  }
  updater(){
    return {
       changename(json,event){
         return event.data;
       }
    }
  }
}

module.exports = User;
